mapper.select:
SELECT
COUNT(CASE WHEN type = 'CREATE_CARD' THEN 1 END) AS createdCards7,
COUNT(CASE WHEN type = 'COMPLETE_CARD' THEN 1 END) AS completedTasks7,
COUNT(CASE WHEN type = 'CREATE_COMMENT' THEN 1 END) AS comments7
FROM activity_log l

    AND l.user_id = #{userId}
    AND l.team_id = #{teamId}
    AND l.board_id = #{boardId}

    AND l.created_at >= DATE_SUB(NOW(), INTERVAL 7 DAY)
