mapper.select:
SELECT * FROM board_member
WHERE board_id = #{boardId}
AND user_id IN

    #{id}

mapper.update:
UPDATE board_member
SET deleted_at = NULL, role = 'MEMBER', joined_at = NOW()
WHERE board_id = #{boardId}
AND user_id IN

    #{id}

UPDATE board_member
SET deleted_at = NULL,
    role       = #{role}
WHERE board_id = #{boardId}
  AND user_id = #{userId}

UPDATE board_member
SET role = #{role}
WHERE board_id = #{boardId}
  AND user_id = #{userId}

UPDATE board_member
SET deleted_at = now()
WHERE board_id = #{boardId}
  AND user_id = #{userId}

mapper.select:
SELECT EXISTS (SELECT 1
               FROM board_member
               WHERE board_id = #{boardId}
                 AND user_id = #{userId}
                 AND deleted_at IS NULL)

SELECT *
FROM board_member
WHERE board_id = #{boardId}
  AND user_id = #{userId}
  AND deleted_at IS NULL
