mapper.update:
UPDATE team_member
SET role = #{role}
WHERE team_id = #{teamId}
  AND user_id = #{userId}

UPDATE team_member
SET deleted_at = now()
WHERE team_id = #{teamId}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(*) > 0
FROM team_member
WHERE team_id = #{teamId}
AND user_id = #{userId}
AND deleted_at IS NULL

SELECT *
FROM team_member
WHERE team_id = #{teamId}
  AND user_id = #{userId}
  AND deleted_at IS NULL
